INSERT [dbo].[QuickHelpEntries] ([Id], [Content]) VALUES (N'14277399-6205-41d7-bdfc-ea0d0e78086c', N'{ "itemtype": "SingleSectionSupportGuide", "title": "Avancerad analys", "headline": "Avancerad analys", "steps": [ {"html": "<h2>Avancerad analys</h2> <p>&nbsp;Klicka p&aring; <strong>Dela mitt skrivbord</strong> s&aring; kommer samtliga deltagare att se ditt skrivbord. En gr&ouml;n flik i sk&auml;rmens &ouml;verkant upplyser om att du delar skrivbordet.<br /> Dessutom placeras etiketten Delad i namnlisten p&aring; varje &ouml;ppet programf&ouml;nster.</p> <p>&nbsp;</p>", "image": "/mediaObject/telia-se/gfx/screenshots/webbmote/telia-webbmote-deladittskrivbord_1/original/telia-webbmote-deladittskrivbord_1.jpg", "isNotStep": "false", "youtubeId": "QWmv7ypITnA"}, {"html": "<p>Om du placerar muspekaren ovanp&aring; den gr&ouml;na fliken f&auml;lls den ut och visar knappar f&ouml;r f&ouml;ljande funktioner:<br /> &bull; <strong>Paus</strong>. Den delade bilden av ditt Skrivbord fryses p&aring; &ouml;vriga deltagares sk&auml;rmar. Du kan allts&aring; utf&ouml;ra olika &aring;tg&auml;rder utan att detta syns. N&auml;r du vill &aring;terta realtidsvis-ningen klickar du p&aring; samma knapp, som nu blinkar och har namnet Forts&auml;tt.<br /> &bull; <strong>Dela.</strong> &Ouml;ppnar en meny med alla delningsalternativ.<br /> &bull; <strong>Tilldela. </strong>&Ouml;ppnar en meny d&auml;r du kan utse en annan deltagare till presentat&ouml;r eller &ouml;verf&ouml;ra kontrollen av tangentbord och mus till en annan deltagare.<br /> &bull;<strong> Ljud</strong>. &Ouml;ppnar dialogrutan f&ouml;r telekonferens.<br /> &bull; <strong>Deltagare, Chatt </strong>och<strong> Kommentera.</strong> &Ouml;ppnar panelerna f&ouml;r deltagare och chatt samt verktygsl&aring;dan f&ouml;r kommentering. Med kommenteringsverktygen kan du peka, rita och skriva direkt p&aring; bilden av skrivbordet.<br /> &bull; <strong>Anteckningar.</strong> &Ouml;ppnar anteckningspanelen.</p> <p><br /> N&auml;r du inte l&auml;ngre vill dela skrivbordet: klicka p&aring; stoppknappen<strong> Avbryt delning.</strong></p>", "image": "/mediaObject/telia-se/gfx/screenshots/webbmote/telia-webbmote-deladittskrivbord_2/original/telia-webbmote-deladittskrivbord_2.jpg", "isNotStep": "false", "youtubeId": ""}, {"html": "<h2>F&ouml;r &ouml;vriga deltagagare:</h2> <p>N&auml;r presentat&ouml;ren startat delningen ser du hans eller hennes skrivbord i sin helhet. En gr&ouml;n flik i sk&auml;rmens &ouml;verkant upplyser om vem som &auml;r &auml;gare till skrivbordet.</p>", "image": "/mediaObject/telia-se/gfx/screenshots/webbmote/telia-webbmote-deladittskrivbord_3/original/telia-webbmote-deladittskrivbord_3.jpg", "isNotStep": "false", "youtubeId": ""}, {"html": "<p>Om du placerar muspekaren ovanp&aring; den gr&ouml;na fliken f&auml;lls den ut och visar knappar f&ouml;r f&ouml;ljande funktioner:<br /> &bull;<strong> Ljud.</strong> &Ouml;ppnar dialogrutan f&ouml;r telekonferens.<br /> &bull; <strong>Deltagare</strong> och <strong>Chatt</strong>. &Ouml;ppnar panelerna f&ouml;r deltagare och chatt.<br /> &bull;<strong> Kommentera</strong>. Be presentat&ouml;ren om tillg&aring;ng till kommenteringsverktygen, s&aring; att du kan du peka, rita och skriva direkt p&aring; bilden av skrivbordet.</p>", "image": "/mediaObject/telia-se/gfx/screenshots/webbmote/telia-webbmote-deladittskrivbord_4/original/telia-webbmote-deladittskrivbord_4.jpg", "isNotStep": "false", "youtubeId": ""} ] }')
INSERT [dbo].[QuickHelpEntries] ([Id], [Content]) VALUES (N'157e91c2-c332-4012-bd3d-88bab82b40f0', N'{ "question": "Vad är analys?", "answer": "<p>&Ouml;verl&aring;telse av abonnemang ska ske skriftligt p&aring; en s&auml;rskild blankett som du  hittar p&aring; v&aring;r hemsida. Observera att en del &auml;ldre abonnemang och oregistrerade  Telia Refill inte kan &ouml;verl&aring;tas.</p>" }')
