INSERT [dbo].[QuickHelpEntries] ([Id], [Content]) VALUES (N'45d3f364-c0f6-4911-adcc-287c3fa0819b', N'{ "question": "Exportera", "answer": "<p>&Ouml;verl&aring;telse av abonnemang ska ske skriftligt p&aring; en s&auml;rskild blankett som du  hittar p&aring; v&aring;r hemsida. Observera att en del &auml;ldre abonnemang och oregistrerade  Telia Refill inte kan &ouml;verl&aring;tas.</p>" }')
INSERT [dbo].[QuickHelpEntries] ([Id], [Content]) VALUES (N'77f2e098-2835-452a-81e0-00b8ea6c9651', N'{ "itemtype": "SingleSectionSupportGuide", "title": "Grundläggande analys", "headline": "Grundläggande analys", "steps": [ {"html": "<p>F&ouml;r att ringa videosamtal beh&ouml;ver du en 3G-mobil med funktion f&ouml;r videosamtal, ett 3G-sim-kort fr&aring;n Telia och 3G-t&auml;ckning.</p> <h2>S&aring; h&auml;r g&ouml;r du</h2> <ol>     <li>Sl&aring; numret eller h&auml;mta det fr&aring;n telefonboken som vid ett vanligt mobilsamtal.</li>     <li>Tryck p&aring; knappen f&ouml;r videosamtal.</li> </ol> <h2>Svara p&aring; ett videosamtal</h2> <p>N&auml;r n&aring;gon ringer ett videosamtal till dig f&aring;r du en fr&aring;ga p&aring; bildsk&auml;rmen om du vill ta samtalet. Tryck p&aring; knappen f&ouml;r att svara, s&aring; kopplas samtalet fram och du kan se den som ringer dig p&aring; bildsk&auml;rmen.</p> <h2>Bra att veta</h2> <p>Om den du ringer till befinner sig utanf&ouml;r 3G-t&auml;ckning eller inte har funktionen p&aring; sin mobiltelefon g&aring;r samtalet inte fram. Du f&aring;r d&aring; en fr&aring;ga om du vill ringa ett vanligt samtal ist&auml;llet.</p>", "image": "/mediaObject/telia-se/pics/miljobilder/mobiltelefoni/videosamtal/original/videosamtal.png", "isNotStep": "true", "youtubeId": ""} ] }')
